mapper.update:
UPDATE POPUPSTORES
SET VIEWS = VIEWS + 1
WHERE STORE_ID = #{storeId}

mapper.select:
SELECT IMAGE_ID, STORE_ID, FILE_PATH
FROM IMAGES
WHERE STORE_ID = #{storeId}

mapper.update:
UPDATE POPUPSTORES
SET status = #{status}
WHERE store_id = #{storeId}

mapper.select:
		SELECT LOCATION
		FROM POPUPSTORES
        WHERE STORE_ID = #{storeId}
